CREATE VIEW `v_dashboarddata` AS 
select  (select  count(0)  from `health_declaration`  where (`health_declaration`.`encounter` = 'yes')) AS `totalencounter`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`vaxxed` = 'yes')) AS `totalvaxxed`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`temp` > '38')) AS `totalfever`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`nationality` <> 'filipino')) AS `totalforeigner`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`nationality` = 'filipino')) AS `totalfilipino`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`age` >= 18)) AS `totaladult`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`age` < 18)) AS `totalminor`,  (select  count(0)  from `health_declaration`) AS `totalrecords`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`gender` = 'male')) AS `totalmale`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`gender` = 'female')) AS `totalfemale`,  (select  count(0)  from `health_declaration`  where (`health_declaration`.`diagnosed` = 'yes')) AS `totaldiagnosed`